    public string MaskReplaceOptimized(string str1, string str2)
    {
        var sb = new StringBuilder(str1);
        for (int i = 0; i < str2.Length; i++)
        {
            if (i >= sb.Length) break;
            if (str2[i] == '*') sb[i] = '*';
        }
        return sb.ToString();
    }

    public StringBuilder ConvertToSimplified(string input)
    {
        StringBuilder sb = new StringBuilder(input);
        for (int i = 0; i < sb.Length; i++)
        {
            if (mapping.TryGetValue(sb[i], out char simplified))
            {
                sb[i] = simplified;
            }
        }
        return sb;
    }

